mapper.select:

select applyNum,  userId,  applyId,
		boardNum, applyContent, applyStatus
from ApplyTable
where boardNum like #{boardNum}
	and applyId like #{applyId}
	and rownum <2

mapper.insert:
insert into ApplyTable(
	userId,  applyId,  boardNum, applyContent
)values(
	#{userId},  #{applyId},  #{boardNum},  #{applyContent}
)

mapper.update:
update boardTable 
set	boardStatus = 'end'
where boardNum = #{num}

update applyTable
set applyStatus = '거절'
where boardNum != #{param2} 
	and applyId != #{param1}

update applyTable
set applyStatus = '승인'
where boardNum = #{param2} 
	and applyId = #{param1}
