mapper.select:
select userId
from userTable
where userId like #{userId}

mapper.insert:
insert into userTable(
	userId, password, name,
	gender, mail, userImage
)values(
	#{userId},#{password},#{name},
	#{gender},#{mail},#{userImage}
)

insert into addressTable(
	userId, addressNumber, address, addressDetail
)values(
	#{userId}, #{addressNumber}, #{address}, #{addressDetail}
)
